mapper.select:
SELECT *
FROM SCHEDULES, (SELECT ATTR_SEQ, ATTR_NAME FROM ATTRACTION) ATTR
WHERE MEMBER_ID LIKE #{id}
    AND PLAN_TITLE LIKE #{title}
    AND SCHEDULES.ATTR_SEQ LIKE ATTR.ATTR_SEQ
ORDER BY SCHEDULE_SEQ

mapper.delete:
DELETE FROM SCHEDULES
WHERE MEMBER_ID LIKE #{id}
    AND PLAN_TITLE LIKE #{title}
